public class ConcurrentProcessor<T, R> {
    private final List<T> inputData;
    private final Function<T, R> processingFunction;
    private final int parallelism;

    public ConcurrentProcessor(List<T> inputData, Function<T, R> processingFunction, int parallelism) {
        this.inputData = inputData;
        this.processingFunction = processingFunction;
        this.parallelism = parallelism;
    }

    public List<R> process() {
        return inputData.parallelStream()
            .parallel()
            .setParallelism(parallelism)
            .map(processingFunction)
            .collect(Collectors.toList());
    }
}
